sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
										   /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
										   /* Optional GlideRecord */ event) {

	// Add your code here
	var subject = 'RE:' + current.event.number + '- 参加する';   
	var body = "Your body text here" + email.watermark; 
	var text='参加する';

	template.print(generateLink(subject, body, text));   

})(current, template, email, email_action, event);


function generateLink(subject, body, text){   
	// The instance has an email address of instance@service-now.com.
	var linkStart = '<a href="mailto:dev50983@service-now.com' +'?subject=' + subject + '&body=' + body + '">';     
	var linkText= text;   
	var linkEnd ='</a>';   

	return linkStart + linkText + linkEnd;   
}
